for(var btn=document.querySelectorAll("button.modal-button"),modals=document.querySelectorAll(".modal"),spans=document.getElementsByClassName("close"),i=0;i<btn.length;i++)btn[i].onclick=function(a){a.preventDefault();modal=document.querySelector(a.target.getAttribute("href"));modal.style.display="block"};for(i=0;i<spans.length;i++)spans[i].onclick=function(){for(var a in modals)if(typeof modals[a].style!=="undefined")modals[a].style.display="none"};
window.onclick=function(a){if(a.target.classList.contains("modal"))for(var b in modals)if(typeof modals[b].style!=="undefined")modals[b].style.display="none"};
(function(){var a=document.querySelector('.toggle-pill-color input[type="checkbox"]'),b=document.body,c=localStorage.getItem("theme")||"dark";if(c=="light")a.removeAttribute("checked");else a.checked="true";b.setAttribute("theme",c);a.addEventListener("change",function(){if(this.checked){b.setAttribute("theme","dark");localStorage.setItem("theme","dark")}else{b.setAttribute("theme","light");localStorage.setItem("theme","light")}},false)})();
